import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { Circle, Rect, Stage, Layer } from 'react-konva';
import Konva from  'konva';

class Snake extends Component{
  state = {
    leap: 30,
    length: 3,
    movement: ["down", "down"],
    positions: [{x: 150, y: 250}, {x: 85, y: 15}, {x: 85, y: 85}],
    color: 'black',
    direction: "down"
  };
  constructor()
  {
    super();
    this.loop();
  }

  move()
  {
    let newhead = this.state.positions[this.state.length-1]
    const [head, ...tail]  = this.state.positions

    if(this.state.direction=="down"){
      newhead.y=newhead.y+this.state.leap
    }
    if(this.state.direction=="up"){
      newhead.y=newhead.y-this.state.leap
    }
    if(this.state.direction=="left"){
      newhead.x=newhead.x-this.state.leap
    }
    if(this.state.direction=="right"){
      newhead.x=newhead.x+this.state.leap
    }
    let newpos = [...tail].push(newhead)
    this.setState({positions: newpos})

  }
  loop(){
    const time = setInterval(()=>{
      let newhead = this.state.positions[this.state.length-1]
      const [head, ...tail]  = this.state.positions

      if(this.state.direction=="down"){
        newhead.y=newhead.y+this.state.leap
      }
      if(this.state.direction=="up"){
        newhead.y=newhead.y-this.state.leap
      }
      if(this.state.direction=="left"){
        newhead.x=newhead.x-this.state.leap
      }
      if(this.state.direction=="right"){
        newhead.x=newhead.x+this.state.leap
      }
      let newpos = [...tail].push(newhead)
      this.setState({positions: newpos})

    },1000)
  }
  render(){
    return(
      <>
      {this.state.positions.map(({x, y}) => (
        <Rect
          x={x}
          y={y}
          width={50}
          height={50}
          fill={this.state.color}
          shadowBlur={5} />

      ))}
      </>
    );
  }


}

class App extends Component {
  render() {
    return (
      <div className="App">
      <p className="App-bg">
          <img src={logo} className="App-logo" alt="logo" />
          <p>Stage starts here</p>
          <Stage width={300} height={300}>
          <Layer>
          <Snake />
          </Layer>
          </Stage>
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
      </p>
      </div>
    );
  }
}

export default App;
