import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { Circle, Rect, Stage, Layer } from 'react-konva';
import Konva from  'konva';

class Snake extends Component{
  state = {
    leap: 30,
    length: 3,
    movement: ["down", "down"],
    positions: [{x: 15, y: 15}, {x: 15, y: 45}, {x: 15, y: 75}],
    color: 'black',
    direction: "down"
  };
  constructor(props)
  {
    super(props);
    this.loop();
  }


  loop(){
    const time = setInterval(()=>{
      let newhead = this.state.positions[this.state.length-1]
      const [head, ...tail]  = this.state.positions

      if(this.state.direction==="down"){
        newhead.y=newhead.y+this.state.leap
      }
      if(this.state.direction==="up"){
        newhead.y=newhead.y-this.state.leap
      }
      if(this.state.direction==="left"){
        newhead.x=newhead.x-this.state.leap
      }
      if(this.state.direction==="right"){
        newhead.x=newhead.x+this.state.leap
      }
      this.setState({positions: [...tail, newhead]})

    },1000)
  }
  render(){
    return(
      <>
      {this.state.positions.map(({x, y}) => (
        <Rect
          x={x}
          y={y}
          width={20}
          height={20}
          fill={this.state.color}
          shadowBlur={5} />

      ))}
      </>
    );
  }


}

class App extends Component {
  render() {
    return (
      <div className="App">
      <p className="App-bg">

          <Stage width={300} height={700}>
          <Layer>
          <Snake />
          </Layer>
          </Stage>


      </p>
      </div>
    );
  }
}

export default App;
